'use client'

import Image from 'next/image'
import { useState } from 'react'
import { Loader2Icon, AlertTriangleIcon } from 'lucide-react'

export default function ImageLoader({ 
  src, 
  alt, 
  width, 
  height 
}) {
  const [isLoading, setIsLoading] = useState(true)
  const [isError, setIsError] = useState(false)

  return (
    <div className="relative w-full h-full min-h-[200px] flex items-center justify-center">
      {isLoading && !isError && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
          <Loader2Icon className="animate-spin text-primary" size={48} />
        </div>
      )}

      {isError && (
        <div className="absolute inset-0 flex flex-col items-center justify-center bg-red-50 text-red-600">
          <AlertTriangleIcon size={48} />
          <p className="mt-2 text-center">Image failed to load</p>
        </div>
      )}

      <Image 
        src={src}
        alt={alt}
        width={width}
        height={height}
        className={`
          ${isLoading ? 'opacity-0' : 'opacity-100'}
          ${isError ? 'hidden' : 'block'}
          transition-opacity duration-300
        `}
        onLoadingComplete={() => setIsLoading(false)}
        onError={() => {
          setIsLoading(false)
          setIsError(true)
        }}
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
        />
</div>
  )}
       